booleanRepresentation:
{
    "$and": [
        {"age between 18 and 79 years old"},
        {"locoregionally recurrent or second primary HNSCC in a previously radiated field"},
        {"underwent surgery with gross total resection and randomized within 8 weeks of surgery"},
        {
            "$and": [
                {"positive margins and/or extra nodal extension (ENE)"},
                {
                    "$or": [
                        {"positive margins defined as malignancy at or within 1 mm of the margin"},
                        {"high grade dysplasia (i.e. carcinoma in situ) at the margin"}
                    ]
                },
                {"ENE may be either gross or microscopic"}
            ]
        },
        {"PD-L1 Combined Positive Score (CPS) >= 1 in a Clinical Laboratory Improvement Act (CLIA) certified laboratory"},
        {"prior radiation to the area of recurrent or second primary tumor > 50% of the presurgical tumor volume received a dose > 45 Gy"},
        {"completed prior radiation a minimum of 6 months prior to randomization"},
        {"no evidence of distant disease based on baseline imaging done within 28 days prior to randomization"},
        {"no anti-PD-1/PD-L1 therapy for recurrent disease"},
        {"Eastern Cooperative Oncology Group (ECOG) performance status 0-1"},
        {
            "$or": [
                {"ability to understand and willingness to sign a written informed consent document"},
                {
                    "$and": [
                        {"impaired decision-making capacity (IDMC)"},
                        {"legally authorized representative (LAR) or caregiver and/or family member available"}
                    ]
                }
            ]
        },
        {
            "$not": {
                "$or": [
                    {"pregnant"},
                    {"breast-feeding"},
                    {
                        "$and": [
                            {"blood test or urine study within 14 days prior to randomization to rule out pregnancy"},
                            {
                                "$or": [
                                    {"urine or serum pregnancy test repeated within 72 hours prior to receiving the first dose of pembrolizumab or chemotherapy"},
                                    {"serum pregnancy test required if urine test is positive or cannot be confirmed as negative"}
                                ]
                            },
                            {
                                "$or": [
                                    {"patient of childbearing potential"},
                                    {"achieved menarche at some point"},
                                    {"not undergone a hysterectomy or bilateral oophorectomy"},
                                    {"not been naturally postmenopausal for at least 24 consecutive months"}
                                ]
                            }
                        ]
                    },
                    {"expect to conceive or father children by using accepted and effective method(s) of contraception or by abstaining from sexual intercourse while on study treatment, and continue for 120 days after the last dose of study treatment"}
                ]
            }
        },
        {"ANC >= 1,500/mcL (obtained =< 28 days prior to protocol randomization)"},
        {"platelets >= 100,000/mcL (obtained =< 28 days prior to protocol randomization)"},
        {"total bilirubin =< 1.5 x institutional upper limit of normal (ULN) (obtained =< 28 days prior to protocol randomization)"},
        {"AST/ALT =< 3.0 x institutional ULN (obtained =< 28 days prior to protocol randomization)"},
        {"creatinine clearance > 30 ml/min using the Cockcroft-Gault formula (obtained =< 28 days prior to protocol randomization)"},
        {"no current active infection that requires systemic treatment at time of randomization"},
        {"no history of non-infectious pneumonitis requiring steroids within 3 years prior to randomization"},
        {"no history of solid organ transplant or stem cell transplant"},
        {
            "$not": {
                "$and": [
                    {"no immunosuppressive medication within 7 days prior to randomization"},
                    {
                        "$or": [
                            {"intranasal, inhaled, topical steroids, or local steroid injection"},
                            {"systemic corticosteroids at physiologic doses =< 10 mg/day of prednisone or equivalent"},
                            {"steroids as premedication for hypersensitivity reactions"}
                        ]
                    }
                ]
            }
        },
        {"clinical risk assessment of cardiac function using the New York Heart Association Functional classification"},
        {"no live vaccine within 30 days prior to the first dose of study drug"},
        {"no severe hypersensitivity (>= grade 3) to pembrolizumab and/or any of its excipients"},
        {
            "$not": {
                "$or": [
                    {"no active autoimmune disease that has required systemic treatment in past 2 years"},
                    {"replacement therapy is not considered a form of systemic treatment"}
                ]
            }
        },
        {"no known psychiatric or substance abuse disorder that would interfere with the participant's ability to cooperate with the requirements of the study"},
        {"HIV-infected patients on effective anti-retroviral therapy with undetectable viral load within 6 months are eligible for this trial"},
        {
            "$not": {
                "$or": [
                    {"known history of hepatitis B (defined as hepatitis B surface antigen [HBsAg] reactive)"},
                    {"known active hepatitis C virus (defined as HCV ribonucleic acid [RNA] [qualitative] is detected) infection"}
                ]
            }
        }
    ]
}
